css:
.text-justify{
        text-align: justify;
    }
    h2{
        display: inline-block;  border-top:1px solid #000; border-bottom:1px solid #000; padding:15px 20px; font-weight: 600 !important; font-size: 22px !important;
    }
    .minwidth-100{
        min-width: 100px;
        display: inline-block;
    }
    .minwidth-200{
        min-width: 200px;
        display: inline-block;
    }
    .minwidth-150{
        min-width: 151px;
        display: inline-block;
    }
    .line_div{
        width:100%;
        padding-bottom:10px;
        font-size:20px;
    }
    .line_div  p.linetext:after {
        content: " ";
        background: #fff;
        width: 100%;
        height: 4px;
        position: absolute;
        left: 0;
        bottom: -3px;
    }
    .line_div  p.linetext{
        margin-bottom: 0;
        display: inline-block;
        z-index: 1;
        position: relative;
        padding-right: 8px;
        font-size: 18px;
        line-height: 24px;
    }
    .underline{
        width: 100%;
        border-bottom:1px solid #000!important;

        z-index: 1;
        
    }
    .font-size-17{
        font-size: 17px;
        display:inline-block;
    }
    .pl-2{
        padding-left: 5px;
    }

html:
    
{% set ctx = get_print_context(**{"print_format":"Bonafide Certificate", "doc":doc}) %}

{% if letter_head %}
    {{ letter_head }}
{% else %}
<br><br><br><br><br><br><br><br><br>
{% endif %}


{% if doc.gender == "Male" %}
    {% set salutation = "Kumar" %}
    {% set relation = "Son" %}
    {% set pronoun1 = "His" %}
    {% set pronoun2 = "He" %}
    {% set pronoun3 = "he" %}
    
{% elif doc.gender == "Female" %}
    {% set salutation = "Kumari" %}
    {% set relation = "Daughter" %}
    {% set pronoun1 = "Her" %}
    {% set pronoun2 = "She" %}
    {% set pronoun3 = "she" %}

{% endif %}

    
<br><br><br><br>
    
    
<div class="container mainpf" style="width:100%;font-size:20px;font-style:none!important;text-align: justify!important;">
        
        <div style="text-align:center;">
            <center><h2 ><i>BONAFIDE CERTIFICATE</i></h2></center>
        </div>
        <br><br>
        

<div class="line_div">
        <font style="font-size:20px;">
            <span>This is to certify that &nbsp;<span class="underline"><b>{{ salutation or '' }} {{doc.student_name}}</b></span> &nbsp;{{ relation }} of &nbsp;
            </span>
        </font>

</div>
<div class="line_div">
    
        <font><span><span class="underline"><b>{{doc.fathers_name or ""}}</b></span> &nbsp; is a bonafied student of our school</span></font>

</div>
<div class="line_div">
    
        <font style="font-size:20px;"><span> since &nbsp;<span class="underline"><b>{{ frappe.format_date(doc.admission_date,'dd-MM-yyyy') }}</b></span> &nbsp; and is presently studying in class <span class="underline"><b>{{ doc.program }}.</b></span></span></font>

</div>
<div class="line_div">
    
        <font style="font-size:20px;"><span>{{pronoun1}} date of birth according to General Register no. &nbsp;<span class="underline"><b>{{doc.gr_number or ""}}</b></span>&nbsp; is </span></font>

</div>
<div class="line_div">
    
        <font style="font-size:20px;"><span> <span class="underline"><b>{{ frappe.format_date(doc.date_of_birth,'dd-MM-yyyy') }} (in words) {{ctx.date_of_birth_in_words}}. </b></span></span></font>

</div>
<div class="line_div">
    
        <font style="font-size:20px;"><span>{{pronoun1}} place of birth is &nbsp;<span class="underline"><b>{{doc.birth_place or ""}}</b></span> &nbsp; and {{pronoun3}} belongs to &nbsp;<span class="underline"><b> {{frappe.db.get_value('Student', doc.student, 'religion') }}&nbsp; ({{doc.category or ""}})</b></span> &nbsp; community.</span></font>

</div>

<div class="line_div">
    <div >
        <p style="font-size:20px;" >{{pronoun2}} is a regular & Bonafide student of this School.</p>
        
    </div>
</div>

</div>

<br><br><br><br><br><br><br><br><br><br><br><br>

<table style="width:100%;">
        <tr style="width:100%;">
            <td style="width:30%;text-align:left!important;font-size:20px;"><b>Issued On:</b></td>
            <td style="width:40%;"></td>
            <td style="width:30%;text-align:right!important;font-size:20px;"><b>Principal's Signature</b></td>
        </tr>
</table>
