css:
.text-justify{
        text-align: justify;
    }
    h2{
        display: inline-block;  border-bottom:1px solid #000; padding:15px 20px; font-weight: 600 !important; font-size: 22px !important;
    }
    .minwidth-100{
        min-width: 100px;
        display: inline-block;
    }
    .minwidth-200{
        min-width: 200px;
        display: inline-block;
    }
    .minwidth-150{
        min-width: 151px;
        display: inline-block;
    }
    .line_div{
        width:100%;
        padding-bottom:5px;
    }
    .line_div  p.linetext:after {
        content: " ";
        background: #fff;
        width: 100%;
        height: 4px;
        position: absolute;
        left: 0;
        bottom: -2px;
    }
    .line_div  p.linetext{
        margin-bottom: 0;
        display: inline-block;
        z-index: 1;
        position: relative;
        padding-right: 8px;
        font-size: 18px;
        line-height: 34px;
    }
    .underline{
        width: 100%;
        border-bottom:1px solid #000;
        z-index: 1;
    }
    .pl-2{
        padding-left: 5px;
    }
